package day6

class Day06(private val input: String) {

    fun solve(messageMarkerSize: Int): Int {
        (1..input.length - messageMarkerSize + 1).forEach {
           if (input.substring(it, it+messageMarkerSize).toSet().size == messageMarkerSize) {
               return it + messageMarkerSize
           }
        }

        return 0
    }
}

fun main(args: Array<String>) {
    val day06 = Day06("")

    val startOfPacketLength = 4
    val startOfMessageLength = 14

    println(day06.solve(startOfPacketLength))
    println(day06.solve(startOfMessageLength))
}